import React, {useEffect, useState} from 'react';
import Header from '../components/Header';
import Footer from '../components/Footer';
import { Container, Row, Col } from 'react-bootstrap';
import Card from 'react-bootstrap/Card';

function Product(){
    const [products, setProducts] = useState([]);
    const[loading,setloading]=useState(true)
    const fetchInfo = async() => { 
        const data=await fetch('https://dummyjson.com/products?limit=10') 
        const productsdata=await data.json()
        setProducts(await productsdata)
        if(await productsdata.products.length > 0){
            setloading(false)
        }
    }
    console.log("productsproducts",products)
    useEffect(() => {
        fetchInfo()
    }, [loading]);
    return (
        <>
            <Header sitename={"My React-App"}/>
                {!loading ? 
                <section className="container">
                    <Container className='my-5'>
                        <Row>
                            {products.products.map((variant, i) => {
                                const {title,description,price,discountPercentage,rating,stock,brand,category,thumbnail} = variant
                                    return <Col className='col-md-3 mb-3' key={i}>
                                                <Card className='text-black'>
                                                    <Card.Body> 
                                                        <div className='product-image mb-3'>
                                                            <img className='w-100' style={{height:"250px"}} src={thumbnail} alt="product-image"/>
                                                        </div>
                                                        <div className='product-content'>
                                                            <div className="text-center">
                                                                <h5 className="card-title text-start">
                                                                    <Card.Title>
                                                                    {title}                                                               </Card.Title>
                                                                </h5>
                                                                <p className="text-muted mb-2 text-start">{description.slice(0,40)}...</p>
                                                            </div>
                                                            <div>
                                                                <div className="d-flex justify-content-between">
                                                                    <div className='product-dis'>
                                                                        <span>Discount(%) </span><span>{discountPercentage}</span>                                                            
                                                                    </div>
                                                                    <div className='product-price'>
                                                                        <span>Price </span><span>{price}</span>                                                              
                                                                    </div>
                                                                </div>

                                                            </div>
                                                            <div className="d-flex justify-content-between total font-weight-bold mt-2">
                                                                <span>Rating </span><span>{rating} Out of 5</span>
                                                            </div>
                                                        </div>
                                                    </Card.Body>
                                                </Card>
                                            </Col>                                
                                    } 
                                )}
                            {/* <Card className='text-black'>
                                <Card.Body>
                                    <div className="text-center">
                                        <h5 className="card-title"><Card.Title>test</Card.Title></h5>
                                        <p className="text-muted mb-4">Apple pro display XDR</p>
                                    </div>
                                    <div>
                                        <div className="d-flex justify-content-between">
                                        <span>Pro Display XDR</span><span>$5,999</span>
                                        </div>
                                        <div className="d-flex justify-content-between">
                                        <span>Pro stand</span><span>$999</span>
                                        </div>
                                        <div className="d-flex justify-content-between">
                                        <span>Vesa Mount Adapter</span><span>$199</span>
                                        </div>
                                    </div>
                                    <div className="d-flex justify-content-between total font-weight-bold mt-4">
                                        <span>Total</span><span>$7,197.00</span>
                                    </div>
                                </Card.Body>
                            </Card> */}
                        </Row>
                    </Container>
                </section>
                :<p style={{fontSize:"25px",textTransform:"uppercase",textAlign:"center"}}>Loading</p>}
            <Footer/>
        </>
    );
}

export default Product;
