import React, {useEffect, useState} from 'react';
import Header from '../components/Header';
import Footer from '../components/Footer';
import { Container, Row, Col } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { useParams } from "react-router-dom";
function Singleproduct(){
    const params = useParams();
    const [ product, setproduct ] = useState();
    const [ loading, setloading ] = useState(true);
    const fetchProduct = async(id) => {
        const data = await fetch(`https://dummyjson.com/products/${id}`)
        const productdata = await data.json()
        setproduct(await productdata)
        if(await productdata.id>0){
            setloading(false)
        }
    }
    console.log(product);
    useEffect(()=>{
        fetchProduct(params.ProductId)
    },[loading]);
    return (
        <>
            <Header sitename={"My Space"}/>
                {!loading ?
                <section className="container">
                    <Container className='my-5 py-3'>
                        <Row>
                            <Col className='col-lg-6 col-md-6 col-12'>
                                <img src={product.images} className="w-100" alt="product-image"/>
                            </Col>
                            <Col className='col-lg-6 col-md-6 col-12'>
                                <div class="product p-4">
                                    <div class="mt-4 mb-3 text-start">
                                        <span class="text-uppercase text-muted brand">{product.brand}</span>
                                        <h5 class="text-uppercase">{product.title}</h5>
                                        <span class="sku  text-start">Sku : {product.sku}</span>
                                        <div class="price d-flex flex-row align-items-center">
                                            <span class="act-price">${product.price}</span>
                                        </div>
                                    </div>
                                    <p class="about  text-start">{product.description}</p>
                                    <img src="" />
                                    <div class="cart mt-4 text-start"> 
                                        <button class="btn btn-danger text-uppercase mr-2 px-4">Add to cart</button> <i class="fa fa-heart text-muted"></i> <i class="fa fa-share-alt text-muted"></i> 
                                    </div>
                                </div>
                            </Col>
                        </Row>
                    </Container>
                </section>
                :<p style={{fontSize:"25px",textTransform:"uppercase",textAlign:"center"}}>Loading</p>}
            <Footer sitename="My Space"/>
        </>
    );
}

export default Singleproduct;
