import React, {useEffect, useState} from 'react';
import Header from '../components/Header';
import Footer from '../components/Footer';
import { Container, Row, Col } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { useParams } from "react-router-dom";
function Singleproduct(){
    const params = useParams();
    const [ product, setproduct ] = useState();
    const [ loading, setloading ] = useState(true);
    const fetchProduct = async(id) => {
        const data = await fetch(`https://dummyjson.com/products/${id}`)
        const productdata = await data.json()
        setproduct(await productdata)
        if(await productdata.id>0){
            setloading(false)
        }
    }
    useEffect(()=>{
        fetchProduct(params.ProductId)
    },[loading]);
    return (
        <>
            <Header sitename={"My Space"}/>
                {!loading ?
                <section className="container">
                    <Container className='my-5 py-3'>
                        <Row>
                            <Col className='col-lg-6 col-md-6 col-12'>
                                {product.title}
                            </Col>
                            <Col className='col-lg-6 col-md-6 col-12'>
                                <div class="product p-4">
                                    <div class="d-flex justify-content-between align-items-center">
                                        <div class="d-flex align-items-center"> <i class="fa fa-long-arrow-left"></i> <span class="ml-1">Back</span> </div>
                                        <i class="fa fa-shopping-cart text-muted"></i>
                                    </div>
                                    <div class="mt-4 mb-3">
                                        <span class="text-uppercase text-muted brand">Orianz</span>
                                        <h5 class="text-uppercase">Men's slim fit t-shirt</h5>
                                        <div class="price d-flex flex-row align-items-center">
                                            <span class="act-price">$20</span>
                                            <div class="ml-2"> <small class="dis-price">$59</small> <span>40% OFF</span> </div>
                                        </div>
                                    </div>
                                    <p class="about">Shop from a wide range of t-shirt from orianz. Pefect for your everyday use, you could pair it with a stylish pair of jeans or trousers complete the look.</p>
                                    <div class="cart mt-4 align-items-center"> <button class="btn btn-danger text-uppercase mr-2 px-4">Add to cart</button> <i class="fa fa-heart text-muted"></i> <i class="fa fa-share-alt text-muted"></i> </div>
                                    </div>
                            </Col>
                        </Row>
                    </Container>
                </section>
                :<p style={{fontSize:"25px",textTransform:"uppercase",textAlign:"center"}}>Loading</p>}
            <Footer sitename="My Space"/>
        </>
    );
}

export default Singleproduct;
