import React, {useEffect, useState} from 'react';
import Header from '../components/Header';
import Footer from '../components/Footer';
import { Container, Row, Col } from 'react-bootstrap';
import Card from 'react-bootstrap/Card';
import Axios from "axios";

function Product(){
    // const [products, setProducts] = useState([]);
    // const fetchInfo = () => { 
    //     return fetch('https://dummyjson.com/products?limit=10') 
    //         .then((res) => res.json()) 
    //         .then((d) => setProducts(d)) 
    // }
    // useEffect(() => {
    //     fetchInfo()
    // }, [])


     const [products, setProducts] = useState([]);

    const fetchProducts = async () => {
        const { data } = await Axios.get(
        "https://dummyjson.com/products?limit=10"
        );
        const products = data;
        setProducts(products);
        console.log(products);
    };

    useEffect(() => {
        fetchProducts();
    }, []);
    
    return (
        <>
            <Header sitename={"My React-App"}/>
                <section className="container">
                    <Container className='my-5'>
                        <Row>
                        {products.map((variant, i) => (
                            test
                        ))}
                            <Card className='text-black'>
                                <Card.Body>
                                    <div className="text-center">
                                        <h5 className="card-title"><Card.Title>test</Card.Title></h5>
                                        <p className="text-muted mb-4">Apple pro display XDR</p>
                                    </div>
                                    <div>
                                        <div className="d-flex justify-content-between">
                                        <span>Pro Display XDR</span><span>$5,999</span>
                                        </div>
                                        <div className="d-flex justify-content-between">
                                        <span>Pro stand</span><span>$999</span>
                                        </div>
                                        <div className="d-flex justify-content-between">
                                        <span>Vesa Mount Adapter</span><span>$199</span>
                                        </div>
                                    </div>
                                    <div className="d-flex justify-content-between total font-weight-bold mt-4">
                                        <span>Total</span><span>$7,197.00</span>
                                    </div>
                                </Card.Body>
                            </Card>
                        </Row>
                    </Container>
                </section>
            <Footer/>
        </>
    );
}

export default Product;
