import React, {useEffect, useState} from 'react';
import Header from '../components/Header';
import Footer from '../components/Footer';
import { Container, Row, Col } from 'react-bootstrap';
import Card from 'react-bootstrap/Card';

function Product(){
   
    
    const url = "https://jsonplaceholder.typicode.com/users";
    const [data, setData] = useState([]);

    const fetchInfo = () => {
        return fetch(url)
        .then((res) => res.json())
        .then((d) => setData(d))
    }


    useEffect(() => {
        fetchInfo();
    }, []);
 
    return (
        <>
            {data.map((dataObj, index) => {
            {dataObj.name}
            })}
            <Header sitename={"My React-App"}/>
                <section className="container">
                    <Container className='my-5'>
                        <Row>
                            <Card className='text-black'>
                                <Card.Body>
                                    <div className="text-center">
                                        <h5 className="card-title"><Card.Title>test</Card.Title></h5>
                                        <p className="text-muted mb-4">Apple pro display XDR</p>
                                    </div>
                                    <div>
                                        <div className="d-flex justify-content-between">
                                        <span>Pro Display XDR</span><span>$5,999</span>
                                        </div>
                                        <div className="d-flex justify-content-between">
                                        <span>Pro stand</span><span>$999</span>
                                        </div>
                                        <div className="d-flex justify-content-between">
                                        <span>Vesa Mount Adapter</span><span>$199</span>
                                        </div>
                                    </div>
                                    <div className="d-flex justify-content-between total font-weight-bold mt-4">
                                        <span>Total</span><span>$7,197.00</span>
                                    </div>
                                </Card.Body>
                            </Card>
                        </Row>
                    </Container>
                </section>
            <Footer/>
        </>
    );
}

export default Product;
